"use strict";

var _ = require("lodash");

var previousConfig;

module.exports = function (userConfig) {
    if (previousConfig) {
        return previousConfig;
    }


    previousConfig = _.extend(
        {
            apiKey: "788cbc3a054cfbfac8b0883eee6d0588"
        },
        userConfig,
        {
            api: {
                "v2.5": {
                    url: "api.openweathermap.org/data/2.5"
                }
            }
        });

    return previousConfig;
};